insert  into `t_help`(`id`,`title`,`content`,`newtime`) values (1,'登录与注册','<p style=\"line-height: 1.75em;\"><strong><span style=\"font-size: 18px; font-family: 宋体;\">如何注册论坛账号</span></strong><span style=\";font-size:16px;font-family:&#39;宋体&#39;\"><br/><br/>&nbsp; &nbsp; &nbsp;点击论坛右上角的&quot;注册&quot;进行注册。注册成功之后，系统会自动跳转到论坛首页。<br/><br/><strong><span style=\"font-size: 18px; font-family: 宋体;\">如何登录论坛</span></strong><br/><br/>&nbsp; &nbsp; &nbsp;如果您已经注册成功，您可以点击页面右上角的登录进行登录，进入登录界面填写正确的用户名、密码，点击&quot;登录&quot;即可完成登录。登录成功后会自动跳转到论坛首页。<br/></span></p><p><br/></p>','2014-05-11 21:11:23'),(2,'密码注意事项','<p style=\"line-height: 1.75em;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: &#39;宋体&#39;;\">忘记密码</span></strong></span><span style=\";font-size:16px;font-family:&#39;宋体&#39;\"><br/><br/>&nbsp;&nbsp;&nbsp; 目前还没有找回密码的功能，所以请大家先牢记自己的密码，论坛会加紧时间进一步开发这个功能，敬请期待，谢谢！<br/><br/><span style=\"font-family: &#39;宋体&#39;; font-size: 18px;\"><strong>修改密码</strong></span><br/><br/>&nbsp;&nbsp;&nbsp; 登录后点击论坛右上角&quot;个人中心&quot;—&quot;修改密码&quot;&nbsp;可以修改登录密码。</span></p><p><br/></p>','2014-08-24 00:49:23'),(3,'如何发表贴子','<p style=\"line-height: 1.75em;\"><span style=\"font-size: 20px;\"><strong><span style=\"font-family: &#39;宋体&#39;;\">发帖</span></strong></span></p><p style=\"line-height: 1.75em;\"><span style=\";font-size:16px;font-family:&#39;宋体&#39;\">&nbsp;&nbsp;&nbsp; <span style=\"font-family: &#39;宋体&#39;; font-size: 18px;\">用户登录后找到相应的发帖按钮如下，</span></span></p><p style=\"line-height: 1.75em;\"><span style=\";font-size:14px;font-family:&#39;Times New Roman&#39;\">&nbsp;</span></p><p style=\"line-height: 1.75em;\"><img src=\"http://localhost:8080/DS-Java/ueditor/jsp/upload1/20140511/19671399812391141.png\" style=\"width: 364px; height: 175px;\" title=\"发帖.png\"/></p><p style=\"line-height: 1.75em;\"><img style=\"width: 508px; height: 101px;\" src=\"http://localhost:8080/DS-Java/ueditor/jsp/upload1/20140511/48051399812630936.png\" title=\"发帖2.png\"/></p><p style=\"line-height: